   547→        }
   548→      }
   549→
   550→      passed = true;
   551→    } catch (err) {
   552→      const error = err instanceof Error ? err : new Error(String(err));
   553→      log.debug('Health check failed', { error: error.message });
   554→      passed = false;
   555→    }
   556→
   557→    // Update failure count
   558→    if (passed) {
   559→      const wasUnhealthy = this.state === 'unhealthy';
   560→
   561→      if (this.consecutiveFailures > 0) {
   562→        log.info('Health check passed, recovering', {
   563→          previousFailures: this.consecutiveFailures,
   564→        });
   565→        this.consecutiveFailures = 0;
   566→      }
   567→
   568→      if (wasUnhealthy) {
   569→        this.transitionState('healthy');
   570→        this.emit('health:status', true, true);
   571→      }
   572→
   573→      this.emit('health:check', true, this.consecutiveFailures);
   574→    } else {
   575→      this.consecutiveFailures++;
   576→      this.emit('health:check', false, this.consecutiveFailures);
   577→
   578→      log.warn('Health check failed', {
   579→        consecutiveFailures: this.consecutiveFailures,
   580→        threshold: this.options.failureThreshold,
   581→      });
   582→
   583→      // Check if we've exceeded the failure threshold
   584→      if (this.consecutiveFailures >= this.options.failureThreshold) {
   585→        if (this.state !== 'unhealthy') {
   586→          this.transitionState('unhealthy');
   587→          this.emit('health:status', false, false);
   588→        }
   589→
   590→        // Terminate and respawn
   591→        log.warn('Failure threshold exceeded, restarting agent');
   592→        await this.restartUnhealthyAgent();
