    37→    max: 60000, // 60 seconds
    38→    multiplier: 2,
    39→  },
    40→} as const;
    41→
    42→/**
    43→ * AgentLifecycle
    44→ *
    45→ * Spawns, monitors, and manages agent processes using ACP client for communication.
    46→ * Follows the ChannelLifecycle pattern for state machine management.
    47→ */
    48→export class AgentLifecycle extends EventEmitter {
    49→  private state: AgentLifecycleState = 'idle';
    50→  private process: ChildProcess | null = null;
    51→  private acpClient: ACPClient | null = null;
    52→  private sessionId: string | undefined;
    53→
    54→  private healthTimer: NodeJS.Timeout | null = null;
    55→  private consecutiveFailures = 0;
    56→  private currentBackoffMs: number;
    57→
    58→  private spawnQueue: QueuedSpawnRequest[] = [];
    59→  private activeSpawns = 0;
    60→
    61→  private readonly options: Required<
    62→    Omit<AgentLifecycleOptions, 'backoff'> & {
    63→      backoff: Required<NonNullable<AgentLifecycleOptions['backoff']>>;
    64→    }
    65→  >;
    66→
    67→  constructor(options: AgentLifecycleOptions) {
    68→    super();
    69→
    70→    this.options = {
    71→      command: options.command,
    72→      args: options.args ?? [],
    73→      cwd: options.cwd ?? process.cwd(),
    74→      env: options.env ?? {},
    75→      healthCheckInterval:
    76→        options.healthCheckInterval ?? DEFAULTS.healthCheckInterval,
    77→      failureThreshold: options.failureThreshold ?? DEFAULTS.failureThreshold,
    78→      shutdownTimeout: options.shutdownTimeout ?? DEFAULTS.shutdownTimeout,
    79→      maxConcurrentSpawns:
    80→        options.maxConcurrentSpawns ?? DEFAULTS.maxConcurrentSpawns,
    81→      backoff: {
    82→        initial: options.backoff?.initial ?? DEFAULTS.backoff.initial,
    83→        max: options.backoff?.max ?? DEFAULTS.backoff.max,
    84→        multiplier: options.backoff?.multiplier ?? DEFAULTS.backoff.multiplier,
    85→      },
    86→    };
    87→
    88→    this.currentBackoffMs = this.options.backoff.initial;
    89→  }
    90→
    91→  /**
    92→   * Get the current lifecycle state
    93→   */
    94→  getState(): AgentLifecycleState {
    95→    return this.state;
    96→  }
    97→
    98→  /**
    99→   * Check if the agent is healthy
   100→   */
   101→  isHealthy(): boolean {
   102→    return this.state === 'healthy';
   103→  }
   104→
   105→  /**
   106→   * Get the ACP client for communication with the agent
   107→   */
   108→  getClient(): ACPClient | null {
   109→    return this.acpClient;
   110→  }
   111→
   112→  /**
   113→   * Get the current session ID if active
   114→   */
   115→  getSessionId(): string | undefined {
   116→    return this.sessionId;
   117→  }
   118→
   119→  /**
   120→   * Spawn the agent process
   121→   *
   122→   * If already spawning or at max concurrent spawns, the request is queued.
   123→   * Environment variables are merged with KYNETIC_* vars.
   124→   *
   125→   * @param env Additional environment variables for this spawn
   126→   */
   127→  async spawn(env?: Record<string, string>): Promise<void> {
   128→    // If we can't spawn right now, queue the request
   129→    if (
   130→      this.activeSpawns >= this.options.maxConcurrentSpawns ||
   131→      this.state === 'spawning'
   132→    ) {
   133→      return new Promise<void>((resolve, reject) => {
   134→        this.spawnQueue.push({ env, resolve, reject });
   135→        const queueLength = this.spawnQueue.length;
   136→        log.warn('Spawn request queued', { queueLength });
   137→        this.emit('spawn:queued', queueLength);
   138→      });
   139→    }
   140→
   141→    // Can't spawn from certain states
   142→    if (
   143→      this.state !== 'idle' &&
   144→      this.state !== 'failed' &&
   145→      this.state !== 'unhealthy'
   146→    ) {
   147→      throw new Error(`Cannot spawn from state: ${this.state}`);
   148→    }
   149→
   150→    await this.performSpawn(env);
   151→  }
   152→
   153→  /**
   154→   * Stop the agent gracefully
   155→   *
   156→   * Sends SIGTERM and waits for shutdown timeout before force-killing.
   157→   */
   158→  async stop(): Promise<void> {
   159→    // Already stopped or stopping
   160→    if (this.state === 'idle' || this.state === 'stopping') {
   161→      return;
   162→    }
   163→
   164→    // If spawning, wait for spawn to complete then stop
   165→    if (this.state === 'spawning') {
   166→      // Wait a bit for spawn to complete
   167→      await new Promise((resolve) => setTimeout(resolve, 100));
   168→      if (this.state === 'spawning') {
   169→        // Still spawning, force kill
   170→        await this.kill();
   171→        return;
   172→      }
   173→    }
   174→
   175→    this.transitionState('stopping');
   176→    this.stopHealthMonitoring();
   177→
   178→    if (!this.process) {
   179→      this.cleanup();
   180→      this.transitionState('idle');
   181→      this.emit('shutdown:complete');
   182→      return;
   183→    }
   184→
   185→    // Close ACP client first (stop accepting new work)
   186→    if (this.acpClient) {
   187→      this.acpClient.close();
   188→    }
   189→
   190→    // Send SIGTERM for graceful shutdown
   191→    this.process.kill('SIGTERM');
   192→
   193→    // Wait for process to exit or timeout
   194→    let timeoutId: NodeJS.Timeout | null = null;
   195→
   196→    const exitPromise = new Promise<void>((resolve) => {
   197→      if (!this.process) {
   198→        resolve();
   199→        return;
   200→      }
   201→
   202→      // Check if already dead BEFORE adding listener (avoids TOCTOU race)
   203→      if (this.process.exitCode !== null || this.process.signalCode !== null) {
   204→        resolve();
   205→        return;
   206→      }
   207→
   208→      this.process.once('exit', () => resolve());
   209→    });
   210→
   211→    const timeoutPromise = new Promise<'timeout'>((resolve) => {
   212→      timeoutId = setTimeout(() => resolve('timeout'), this.options.shutdownTimeout);
   213→    });
   214→
   215→    const result = await Promise.race([exitPromise, timeoutPromise]);
   216→
   217→    if (timeoutId !== null) {
   218→      clearTimeout(timeoutId);
   219→    }
   220→
   221→    if (result === 'timeout') {
   222→      log.warn('Graceful shutdown timeout, force killing', {
   223→        timeout: this.options.shutdownTimeout,
   224→      });
   225→      await this.kill();
   226→    } else {
   227→      // Emit shutdown:complete BEFORE cleanup (which removes listeners)
   228→      this.transitionState('idle');
   229→      this.emit('shutdown:complete');
   230→      this.cleanup();
   231→    }
   232→  }
   233→
   234→  /**
   235→   * Force kill the agent process
   236→   */
   237→  async kill(): Promise<void> {
   238→    if (!this.process) {
   239→      this.cleanup();
   240→      if (this.state !== 'idle') {
   241→        this.transitionState('idle');
   242→      }
   243→      return;
   244→    }
   245→
   246→    this.transitionState('terminating');
   247→    this.stopHealthMonitoring();
   248→
   249→    // Close ACP client
   250→    if (this.acpClient) {
   251→      this.acpClient.close();
   252→    }
   253→
   254→    // Force kill
   255→    this.process.kill('SIGKILL');
   256→
   257→    // Wait for exit
   258→    await new Promise<void>((resolve) => {
   259→      if (!this.process) {
   260→        resolve();
   261→        return;
   262→      }
   263→
   264→      // Check if already dead BEFORE adding listener (avoids TOCTOU race)
   265→      if (this.process.exitCode !== null || this.process.signalCode !== null) {
   266→        resolve();
   267→        return;
   268→      }
   269→
   270→      this.process.once('exit', () => resolve());
   271→    });
   272→
   273→    // Emit shutdown:complete BEFORE cleanup (which removes listeners)
   274→    this.transitionState('idle');
   275→    this.emit('shutdown:complete');
   276→    this.cleanup();
   277→  }
   278→
   279→  /**
   280→   * Create a checkpoint of current state
   281→   */
   282→  getCheckpoint(): AgentCheckpoint {
   283→    const checkpoint: AgentCheckpoint = {
   284→      timestamp: Date.now(),
   285→      state: this.state,
   286→      sessionId: this.sessionId,
   287→      consecutiveFailures: this.consecutiveFailures,
   288→      currentBackoffMs: this.currentBackoffMs,
   289→    };
   290→
   291→    this.emit('checkpoint:saved', checkpoint);
   292→    return checkpoint;
   293→  }
   294→
   295→  /**
   296→   * Restore from a checkpoint
   297→   *
   298→   * Note: This only restores state metadata. The actual process
   299→   * must be re-spawned separately.
   300→   *
   301→   * @returns true if restoration succeeded, false if not in idle state
   302→   */
   303→  restoreFromCheckpoint(checkpoint: AgentCheckpoint): boolean {
   304→    // Only restore if in idle state
   305→    if (this.state !== 'idle') {
   306→      log.warn('Cannot restore checkpoint, not in idle state', {
   307→        currentState: this.state,
   308→      });
   309→      return false;
   310→    }
   311→
   312→    this.consecutiveFailures = checkpoint.consecutiveFailures;
   313→    this.currentBackoffMs = checkpoint.currentBackoffMs;
   314→    this.sessionId = checkpoint.sessionId;
   315→
   316→    log.info('Restored from checkpoint', {
   317→      timestamp: checkpoint.timestamp,
   318→      savedState: checkpoint.state,
   319→      consecutiveFailures: checkpoint.consecutiveFailures,
   320→    });
   321→    return true;
   322→  }
   323→
   324→  /**
   325→   * Perform the actual spawn operation
   326→   */
   327→  private async performSpawn(env?: Record<string, string>): Promise<void> {
   328→    this.activeSpawns++;
   329→    this.transitionState('spawning');
   330→
   331→    try {
   332→      // Build environment with KYNETIC_* vars
   333→      const kyneticEnv: Record<string, string> = {
   334→        KYNETIC_AGENT: 'true',
   335→        KYNETIC_SESSION_ID: this.sessionId ?? '',
   336→      };
   337→
   338→      const mergedEnv = {
   339→        ...process.env,
   340→        ...kyneticEnv,
   341→        ...this.options.env,
   342→        ...env, // Custom env overrides KYNETIC_*
   343→      };
   344→
   345→      // Create pass-through streams for stdio
   346→      const stdinStream = new PassThrough();
   347→      const stdoutStream = new PassThrough();
   348→
   349→      // Spawn the process
   350→      log.info('Spawning agent process', {
   351→        command: this.options.command,
   352→        args: this.options.args,
   353→        cwd: this.options.cwd,
   354→      });
   355→
   356→      this.process = spawn(this.options.command, this.options.args, {
   357→        cwd: this.options.cwd,
   358→        env: mergedEnv as NodeJS.ProcessEnv,
   359→        stdio: ['pipe', 'pipe', 'inherit'],
   360→      });
   361→
   362→      // Wire up stdio streams
   363→      if (this.process.stdin) {
   364→        stdinStream.pipe(this.process.stdin);
   365→      }
   366→      if (this.process.stdout) {
   367→        this.process.stdout.pipe(stdoutStream);
   368→      }
   369→
   370→      // Handle early exit during spawn
   371→      const earlyExitPromise = new Promise<'exited'>((resolve) => {
   372→        this.process?.once('exit', () => resolve('exited'));
   373→      });
   374→
   375→      // Set up exit handler
   376→      this.process.on('exit', (code, signal) => {
   377→        this.handleProcessExit(code, signal);
   378→      });
   379→
   380→      this.process.on('error', (err) => {
   381→        this.handleProcessError(err);
   382→      });
   383→
   384→      // Create ACP client with the process streams
   385→      this.acpClient = new ACPClient({
   386→        stdin: stdoutStream, // Agent's stdout is our stdin
   387→        stdout: stdinStream, // Our stdout is agent's stdin
   388→        clientInfo: {
   389→          name: 'kynetic-bot',
   390→          version: '0.0.0',
   391→        },
   392→        handlers: this.createACPHandlers(),
   393→      });
   394→
   395→      // Wire up ACP events
   396→      this.acpClient.on('close', () => {
   397→        log.debug('ACP client closed');
   398→      });
   399→
   400→      this.acpClient.on('error', (err: Error) => {
   401→        this.emitError(err, { source: 'acp-client' });
   402→      });
   403→
   404→      // Initialize the agent - race with early exit
   405→      const initPromise = this.acpClient.initialize();
   406→      const result = await Promise.race([initPromise, earlyExitPromise]);
   407→
   408→      if (result === 'exited') {
   409→        throw new Error('Agent process exited during initialization');
   410→      }
   411→
   412→      // Success!
   413→      this.transitionState('healthy');
   414→      this.consecutiveFailures = 0;
   415→      this.currentBackoffMs = this.options.backoff.initial;
   416→
   417→      const pid = this.process.pid;
   418→      if (pid === undefined) {
   419→        throw new Error('Process spawned but PID is undefined');
   420→      }
   421→      log.info('Agent spawned successfully', { pid });
   422→      this.emit('agent:spawned', pid);
   423→
   424→      // Start health monitoring
   425→      this.startHealthMonitoring();
   426→
   427→      // Process queued spawn requests
   428→      this.processSpawnQueue();
   429→    } catch (err) {
   430→      const error = err instanceof Error ? err : new Error(String(err));
   431→      log.error('Spawn failed', { error: error.message });
   432→      this.emitError(error, { phase: 'spawn' });
   433→
   434→      // Clean up failed spawn
   435→      if (this.process) {
   436→        this.process.kill('SIGKILL');
   437→        this.process = null;
   438→      }
   439→      if (this.acpClient) {
   440→        this.acpClient.close();
   441→        this.acpClient = null;
   442→      }
   443→
   444→      this.transitionState('failed');
   445→
   446→      // Apply backoff
   447→      this.currentBackoffMs = Math.min(
   448→        this.currentBackoffMs * this.options.backoff.multiplier,
   449→        this.options.backoff.max,
   450→      );
   451→
   452→      // Reject queued spawns on failure
   453→      this.rejectSpawnQueue(error);
   454→
   455→      throw error;
   456→    } finally {
   457→      this.activeSpawns--;
   458→    }
   459→  }
   460→
   461→  /**
   462→   * Process queued spawn requests
   463→   */
   464→  private processSpawnQueue(): void {
   465→    while (
   466→      this.spawnQueue.length > 0 &&
   467→      this.activeSpawns < this.options.maxConcurrentSpawns
   468→    ) {
   469→      const request = this.spawnQueue.shift()!;
   470→      const queueLength = this.spawnQueue.length;
   471→
   472→      log.info('Processing queued spawn request', { queueLength });
   473→      this.emit('spawn:dequeued', queueLength);
   474→
   475→      // Note: We don't await here since we want to continue processing
   476→      this.performSpawn(request.env)
   477→        .then(() => request.resolve())
   478→        .catch((err: Error) => request.reject(err));
   479→    }
   480→  }
   481→
   482→  /**
   483→   * Reject all queued spawn requests
   484→   */
   485→  private rejectSpawnQueue(error: Error): void {
   486→    for (const request of this.spawnQueue) {
   487→      request.reject(error);
   488→    }
   489→    this.spawnQueue = [];
   490→  }
   491→
   492→  /**
   493→   * Start health monitoring
   494→   */
   495→  private startHealthMonitoring(): void {
   496→    if (this.healthTimer) {
   497→      return;
   498→    }
   499→
   500→    this.healthTimer = setInterval(() => {
   501→      void this.performHealthCheck();
   502→    }, this.options.healthCheckInterval);
   503→  }
   504→
   505→  /**
   506→   * Stop health monitoring
   507→   */
   508→  private stopHealthMonitoring(): void {
   509→    if (this.healthTimer) {
   510→      clearInterval(this.healthTimer);
   511→      this.healthTimer = null;
   512→    }
   513→  }
   514→
   515→  /**
   516→   * Perform a health check
   517→   *
   518→   * Health is determined by:
   519→   * 1. Process is alive (exitCode is null)
   520→   * 2. ACP client has a valid session
   521→   */
   522→  private async performHealthCheck(): Promise<void> {
   523→    if (this.state !== 'healthy' && this.state !== 'unhealthy') {
   524→      return;
   525→    }
   526→
   527→    let passed = false;
   528→
   529→    try {
   530→      // Check 1: Process is alive
   531→      if (!this.process || this.process.exitCode !== null) {
   532→        throw new Error('Process is not running');
   533→      }
   534→
   535→      // Check 2: ACP client exists and has sessions
   536→      // Note: ACP doesn't have a dedicated ping, so we check if client exists
   537→      // and is not closed. The session state check acts as a liveness proxy.
   538→      if (!this.acpClient) {
   539→        throw new Error('ACP client not available');
   540→      }
   541→
   542→      // If we have a session, verify it still exists
   543→      if (this.sessionId) {
   544→        const session = this.acpClient.getSession(this.sessionId);
   545→        if (!session) {
   546→          throw new Error(`Session ${this.sessionId} no longer exists`);
   547→        }
   548→      }
   549→
   550→      passed = true;
   551→    } catch (err) {
   552→      const error = err instanceof Error ? err : new Error(String(err));
   553→      log.debug('Health check failed', { error: error.message });
   554→      passed = false;
   555→    }
   556→
   557→    // Update failure count
   558→    if (passed) {
   559→      const wasUnhealthy = this.state === 'unhealthy';
   560→
   561→      if (this.consecutiveFailures > 0) {
   562→        log.info('Health check passed, recovering', {
   563→          previousFailures: this.consecutiveFailures,
   564→        });
   565→        this.consecutiveFailures = 0;
   566→      }
   567→
   568→      if (wasUnhealthy) {
   569→        this.transitionState('healthy');
   570→        this.emit('health:status', true, true);
   571→      }
   572→
   573→      this.emit('health:check', true, this.consecutiveFailures);
   574→    } else {
   575→      this.consecutiveFailures++;
   576→      this.emit('health:check', false, this.consecutiveFailures);
   577→
   578→      log.warn('Health check failed', {
   579→        consecutiveFailures: this.consecutiveFailures,
   580→        threshold: this.options.failureThreshold,
   581→      });
   582→
   583→      // Check if we've exceeded the failure threshold
   584→      if (this.consecutiveFailures >= this.options.failureThreshold) {
   585→        if (this.state !== 'unhealthy') {
   586→          this.transitionState('unhealthy');
   587→          this.emit('health:status', false, false);
   588→        }
   589→
   590→        // Terminate and respawn
   591→        log.warn('Failure threshold exceeded, restarting agent');
   592→        await this.restartUnhealthyAgent();
   593→      }
   594→    }
   595→  }
   596→
   597→  /**
   598→   * Restart an unhealthy agent
   599→   */
   600→  private async restartUnhealthyAgent(): Promise<void> {
   601→    // Stop current process
   602→    await this.kill();
   603→
   604→    // Wait for backoff
   605→    log.info('Waiting for backoff before respawn', {
   606→      backoffMs: this.currentBackoffMs,
   607→    });
   608→    await new Promise((resolve) =>
   609→      setTimeout(resolve, this.currentBackoffMs),
   610→    );
   611→
   612→    // Try to respawn
   613→    try {
   614→      await this.performSpawn();
   615→    } catch {
   616→      // Check if we should escalate
   617→      if (this.currentBackoffMs >= this.options.backoff.max) {
   618→        log.error('Max backoff reached, escalating');
   619→        this.emit('escalate', 'Max backoff reached after repeated spawn failures', {
   620→          backoffMs: this.currentBackoffMs,
   621→          consecutiveFailures: this.consecutiveFailures,
   622→        });
   623→      }
   624→    }
   625→  }
   626→
   627→  /**
   628→   * Handle process exit
   629→   */
   630→  private handleProcessExit(
   631→    code: number | null,
   632→    signal: NodeJS.Signals | null,
   633→  ): void {
   634→    log.info('Agent process exited', { code, signal });
   635→    this.emit('agent:exited', code, signal);
   636→
   637→    // Don't trigger respawn during intentional shutdown
   638→    if (this.state === 'stopping' || this.state === 'terminating') {
   639→      return;
   640→    }
   641→
   642→    // If we were healthy, this is unexpected - mark as unhealthy
   643→    if (this.state === 'healthy' || this.state === 'unhealthy') {
   644→      this.transitionState('unhealthy');
   645→
   646→      // Trigger respawn
   647→      void this.restartUnhealthyAgent();
   648→    }
   649→  }
   650→
   651→  /**
   652→   * Handle process error
   653→   */
   654→  private handleProcessError(err: Error): void {
   655→    log.error('Agent process error', { error: err.message });
   656→    this.emitError(err, { source: 'process' });
   657→  }
   658→
   659→  /**
   660→   * Transition to a new state
   661→   */
   662→  private transitionState(newState: AgentLifecycleState): void {
   663→    const oldState = this.state;
   664→    if (oldState === newState) {
   665→      return;
   666→    }
   667→
   668→    log.debug('State transition', { from: oldState, to: newState });
   669→    this.state = newState;
   670→    this.emit('state:change', oldState, newState);
   671→  }
   672→
   673→  /**
   674→   * Emit an error event with context
   675→   */
   676→  private emitError(error: Error, context: Record<string, unknown>): void {
   677→    this.emit('error', error, {
   678→      state: this.state,
   679→      consecutiveFailures: this.consecutiveFailures,
   680→      ...context,
   681→    });
   682→  }
   683→
   684→  /**
   685→   * Clean up resources
   686→   */
   687→  private cleanup(): void {
   688→    // Clear timers
   689→    this.stopHealthMonitoring();
   690→
   691→    // Remove ACP client listeners before nulling to prevent accumulation
   692→    if (this.acpClient) {
   693→      this.acpClient.removeAllListeners();
   694→      this.acpClient.close();
   695→    }
   696→
   697→    // Clear references
   698→    this.process = null;
   699→    this.acpClient = null;
   700→    this.sessionId = undefined;
   701→
   702→    // Don't remove user-attached listeners - instance remains usable
   703→  }
   704→
   705→  /**
   706→   * Create ACP handlers for file operations and permissions
   707→   */
   708→  /**
   709→   * Create ACP handlers for file operations and permissions
   710→   */
   711→  private createACPHandlers(): ACPClientHandlers {
   712→    return {
   713→      // AC: @agent-lifecycle ac-5 - Handle file read requests from agent
   714→      readFile: async (params) => {
   715→        log.debug('Reading file for agent', { path: params.path });
   716→        try {
   717→          const content = await fs.readFile(params.path, 'utf8');
