   114→      } else {
   115→        _exitCode = 0;
   116→        _signalCode = 'SIGTERM';
   117→      }
   118→      // Emit exit event asynchronously to allow test assertions
   119→      setImmediate(() => {
   120→        processEmitter.emit('exit', _exitCode, _signalCode);
   121→      });
   122→      return true;
   123→    }),
   124→
   125→    // Test helpers
   126→    _emit: (event: string, ...args: unknown[]) => {
   127→      processEmitter.emit(event, ...args);
   128→    },
   129→
   130→    _setExitCode: (code: number | null) => {
   131→      _exitCode = code;
   132→    },
   133→  });
   134→
   135→  return mockProcess;
   136→}
