     1→/**
     2→ * Supervisor - Process lifecycle manager for kbot
     3→ *
     4→ * Spawns kbot as a child process with IPC channel, handles signals,
     5→ * manages respawn with exponential backoff, and coordinates graceful shutdown.
     6→ *
     7→ * @see @supervisor-process-spawn
     8→ */
     9→
    10→import { EventEmitter } from 'node:events';
    11→import { fork, type ChildProcess } from 'node:child_process';
    12→import { writeFile } from 'node:fs/promises';
    13→import { createLogger, type Logger } from '@kynetic-bot/core';
    14→import type { IpcMessage, Checkpoint, RestartReason } from './schemas.js';
    15→
    16→export interface SupervisorConfig {
    17→  /**
    18→   * Path to the child process executable (e.g., kbot)
    19→   */
    20→  childPath: string;
    21→
    22→  /**
    23→   * Arguments to pass to the child process
    24→   */
    25→  childArgs?: string[];
    26→
    27→  /**
    28→   * Initial checkpoint path (for wake-up after restart)
    29→   */
    30→  checkpointPath?: string;
    31→
    32→  /**
    33→   * Minimum backoff delay in milliseconds (default: 1000)
    34→   */
    35→  minBackoffMs?: number;
    36→
    37→  /**
    38→   * Maximum backoff delay in milliseconds (default: 60000)
    39→   */
    40→  maxBackoffMs?: number;
    41→
    42→  /**
    43→   * Graceful shutdown timeout in milliseconds (default: 30000)
    44→   */
    45→  shutdownTimeoutMs?: number;
    46→}
    47→
    48→export interface SupervisorEvents {
    49→  /**
    50→   * Emitted when child process is spawned
    51→   * AC: @supervisor-process-spawn ac-6
    52→   */
    53→  spawn: (pid: number) => void;
    54→
    55→  /**
    56→   * Emitted when child process exits
    57→   */
    58→  exit: (code: number | null, signal: string | null) => void;
    59→
    60→  /**
    61→   * Emitted when respawning after crash
    62→   * AC: @supervisor-process-spawn ac-4
    63→   */
    64→  respawn: (attempt: number, backoffMs: number) => void;
    65→
    66→  /**
    67→   * Emitted when backoff reaches maximum
    68→   * AC: @supervisor-process-spawn ac-5
    69→   */
    70→  escalation: (consecutiveFailures: number) => void;
    71→
    72→  /**
    73→   * Emitted when IPC channel setup fails
    74→   * AC: @supervisor-process-spawn ac-7
    75→   */
    76→  ipc_error: (error: Error) => void;
    77→
    78→  /**
    79→   * Emitted when shutdown completes
    80→   */
    81→  shutdown: () => void;
    82→}
    83→
    84→// eslint-disable-next-line @typescript-eslint/no-unsafe-declaration-merging
    85→export declare interface Supervisor {
    86→  on<E extends keyof SupervisorEvents>(event: E, listener: SupervisorEvents[E]): this;
    87→  emit<E extends keyof SupervisorEvents>(
    88→    event: E,
    89→    ...args: Parameters<SupervisorEvents[E]>
    90→  ): boolean;
    91→}
    92→
    93→/**
    94→ * Supervisor manages the kbot process lifecycle
    95→ *
    96→ * AC: @supervisor-process-spawn ac-1 through ac-9
    97→ * AC: @trait-observable ac-1, ac-2, ac-3
    98→ * AC: @trait-graceful-shutdown ac-1, ac-2, ac-3
    99→ */
   100→// eslint-disable-next-line @typescript-eslint/no-unsafe-declaration-merging
   101→export class Supervisor extends EventEmitter {
   102→  private log: Logger;
   103→  private config: Required<SupervisorConfig>;
   104→  private child: ChildProcess | null = null;
   105→  private isShuttingDown = false;
   106→  private currentBackoffMs: number;
   107→  private consecutiveFailures = 0;
   108→  private lastSpawnTime: number | null = null;
   109→  private pendingCheckpointPath: string | null = null;
   110→
   111→  constructor(config: SupervisorConfig) {
   112→    super();
   113→    this.log = createLogger('supervisor');
   114→
