   116→    this.config = {
   117→      childPath: config.childPath,
   118→      childArgs: config.childArgs ?? [],
   119→      checkpointPath: config.checkpointPath ?? '',
   120→      minBackoffMs: config.minBackoffMs ?? 1000,
   121→      maxBackoffMs: config.maxBackoffMs ?? 60000,
   122→      shutdownTimeoutMs: config.shutdownTimeoutMs ?? 30000,
   123→    };
   124→
   125→    this.currentBackoffMs = this.config.minBackoffMs;
   126→
   127→    this.log.info('Supervisor initialized', {
   128→      childPath: this.config.childPath,
   129→      checkpointPath: this.config.checkpointPath || '(none)',
