   135→   *
   136→   * AC: @supervisor-process-spawn ac-1, ac-6
   137→   */
   138→  async spawn(): Promise<void> {
   139→    if (this.isShuttingDown) {
   140→      this.log.warn('Cannot spawn during shutdown');
   141→      return;
   142→    }
   143→
   144→    if (this.child) {
   145→      this.log.warn('Child process already running', { pid: this.child.pid });
   146→      return;
   147→    }
   148→
   149→    try {
   150→      this.lastSpawnTime = Date.now();
   151→
   152→      // Build args - include checkpoint if available
   153→      const args = [...this.config.childArgs];
   154→      if (this.config.checkpointPath) {
   155→        args.push('--checkpoint', this.config.checkpointPath);
   156→      }
   157→
   158→      // Spawn with IPC channel
   159→      this.log.info('Spawning child process', {
   160→        childPath: this.config.childPath,
   161→        args,
   162→      });
