   187→      const error = err instanceof Error ? err : new Error(String(err));
   188→
   189→      // AC: @supervisor-process-spawn ac-7
   190→      this.log.error('Failed to spawn child process', {
   191→        error: error.message,
   192→        stack: error.stack,
   193→      });
   194→
   195→      this.emit('ipc_error', error);
   196→      this.child = null;
   197→
