      process.env.AGENT_COMMAND = 'test-command';
      process.env.HEALTH_CHECK_INTERVAL = '30000.5';

      expect(() => loadConfig()).toThrow('Invalid integer for HEALTH_CHECK_INTERVAL');
    });
  });
});

